/*eslint-disable*/
import React from "react";
import { NavLink } from "react-router-dom";
import { Nav } from "reactstrap";
// javascript plugin used to create scrollbars on windows
import PerfectScrollbar from "perfect-scrollbar";
import {
  InputGroup,
  InputGroupText,
  InputGroupAddon,
  Input,
} from "reactstrap";
import logo from '../../assets/img/그림1.png';

var ps;
console.important = function(text) 
{
  ReactDOM.render(
    InputGroupText.props.Input
  );
}
function Sidebar(props) {
  const sidebar = React.useRef();
  // verifies if routeName is the one active (in browser input)
  const activeRoute = (routeName) => {
    return props.location.pathname.indexOf(routeName) > -1 ? "active" : "";
  };
  React.useEffect(() => 
  {
    const script = document.createElement("script");
    let tt = 2;
    let arr = [1,2,3,4];
    script.innerHTML += `let abc = 1;`;
    sessionStorage.setItem("test",arr);
    document.head.appendChild(script);
    if (navigator.platform.indexOf("Win") > -1) {
      ps = new PerfectScrollbar(sidebar.current, {
        suppressScrollX: true,
        suppressScrollY: false,
      });
    }
    return function cleanup() {
      if (navigator.platform.indexOf("Win") > -1) {
        ps.destroy();
      }
    };
  });
  return (
    <div className="sidebar" data-color={props.backgroundColor}>
      <div className="logo side-logo map-side">
        <a href="/jeju" className="navbar-brand d-flex align-items-center text-center py-0 px-4 px-lg-5">
          <img class="logo" src={logo}/>
        </a>
      </div>
      <div className="sidebar-wrapper" ref={sidebar}>
        {/* 왼쪽 메뉴 부분 출력하는 부분 */}
        <p id="result2" style={{minHeight:"300px"}}/>
        <div className="maplast-section">
          <button className="map-applybtn" onClick={()=>Sendinfo()}>적용</button>
          <p id="init_map"/>
        </div>
      </div>
    </div>
  );
}

export default Sidebar;
